'use strict';

const fs = require('fs');
const path = require('path');
const punsLength = 129;

let punsPath = function () {
  return path.join(__dirname, './puns.json');
};

let getPun = function (n) {
  return new Promise((resolve, reject) => {
    fs.readFile(punsPath(), 'utf8', (err, data) => {
      if (err) reject(err);
      if (n >= punsLength) resolve(null);

      let puns = JSON.parse(data);

      resolve(puns[n]);
    });
  });
};

let getRandomPun = function () {
  return getPun(Math.floor(Math.random() * punsLength));
};

module.exports = {
  getPun,
  getRandomPun
};
