'use strict';

const cowsay = require('cowsay');
const monowrap = require('monowrap');

let displayCow = function (pun) {
  let output = monowrap(pun, {
    width: 30
  });

  console.log(cowsay.say({
    text: output
  }));
};

let display = function (cow, pun) {
  if (cow) {
    displayCow(pun);
  } else {
    let output = monowrap(pun, {
      width: process.stdout.columns
    });

    console.log(output);
  }

  return;
};

module.exports = display;
